                  }}
                  className="APinput"
                  placeholder="Description"
                  type="text"
                  value={values.description}
                  name="description"
                  onChange={handleChange}
                />
              </div>
              <div className="inputWithText locationInput">
                <span>Enter the Location</span>
                <input
                  className="APinput"
                  placeholder="Area"
                  type="area"
                  value={values.area}
                  name="area"
                  onChange={handleChange}
                />
                <input
                  className="APinput"
                  placeholder="City"
                  type="text"
                  value={values.city}
                  name="city"
                  onChange={handleChange}
                />
                <input
                  className="APinput"
                  placeholder="State"
                  type="text"
                  value={values.state}
                  name="state"
                  onChange={handleChange}
