import React, { useState } from "react";
import { Formik } from "formik";
import { useSelector } from "react-redux";
import { ToastContainer } from "react-toastify";
import * as yup from "yup";
import Notify from "../../components/Notify";

const AddPage = () => {
  const userId = useSelector((state) => state.user._id);

  const initialValues = {
    category: "pg",
    no_of_rooms: "1",
    no_of_bathrooms: "1",
    food_categories: "veg",
    image_path: "",
    description: "",
    area: "",
    city: "",
    state: "",
    reviews: "",
  };

  const [img, setImg] = useState("");

  const handleFormSubmit = async (values, onsubmitProps) => {
    try {
      var house = `{
        "userId": "${userId}",
        "category": "${values.category}",
        "no_of_rooms": "${values.no_of_rooms}",
        "no_of_bathrooms": "${values.no_of_bathrooms}",
        "food_categories": [
            "${values.food_categories}"
        ],
        "image_path": [
            "${values.image_path}"
        ],
        "description": "${values.description}",
        "location": {
            "area": "${values.area}",
            "city": "${values.city}",
            "state": "${values.state}"
        },
        "reviews": []
       }`;

      const formdata = new FormData();
      formdata.append("userId", userId);
      formdata.append("category", values.category);
      formdata.append("no_of_rooms", values.no_of_rooms);
      formdata.append("no_of_bathrooms", values.no_of_bathrooms);
      let food = [values.no_of_bathrooms];
      formdata.append("food_categories", food);
      formdata.append("image_path", img);
      formdata.append("description", values.description);
      let location = {
        area: values.area,
        city: values.city,
        state: values.state,
      };
      formdata.append("location", location);
      console.log(formdata);
      const res = await fetch("http://localhost:3001/house/addhouse/", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: formdata,
      });
      if (res.ok) {
        Notify("Add Created Successfully");
      }
    } catch (e) {
      console.log("error", e);
    }
  };
  return (
    <>
      <ToastContainer />
      <div className="filterhandles">
        <div className="headText">Create Your Own Advertisement</div>
      </div>
      <Formik initialValues={initialValues} onSubmit={handleFormSubmit}>
        {({
          values,
          errors,
          touched,
          handleBlur,
          handleSubmit,
          handleChange,
        }) => (
          <form onSubmit={handleSubmit}>
            <select
              name="category"
              value={values.category}
              onChange={handleChange}
            >
              <option>PG</option>
              <option>Hostel</option>
              <option>Flat</option>
              <option>Home</option>
            </select>
            <input
              type="number"
              value={values.no_of_rooms}
              name="no_of_rooms"
              onChange={handleChange}
            />
            <input
              type="number"
              value={values.no_of_bathrooms}
              name="no_of_bathrooms"
              onChange={handleChange}
            />
            <select
              name="food_categories"
              value={values.food_categories}
              onChange={handleChange}
            >
              <option>VEG</option>
              <option>NON VEG</option>
              <option>JAIN FOOD</option>
            </select>
            {/* image
            <input
              type="text"
              value={values.image_path}
              name="image_path"
              onChange={handleChange}
            /> */}
            image
            <input
              type="file"
              value={undefined}
              name="image_path"
              onChange={handleChange}
            />
            desc
            <input
              type="text"
              value={values.description}
              name="description"
              onChange={handleChange}
            />
            area
            <input
              type="area"
              value={values.area}
              name="area"
              onChange={handleChange}
            />
            city
            <input
              type="text"
              value={values.city}
              name="city"
              onChange={(e) => setImg(e.target.files[0])}
            />
            state
            <input
              type="text"
              value={values.state}
              name="state"
              onChange={handleChange}
            />
            <button type="submit">submit</button>
          </form>
        )}
      </Formik>
    </>
  );
};

export default AddPage;
