import React from "react";
import "../styles/login.css";
import "../styles/inputField/inputField1.css";
import logo from "../assets/logo.svg";
import { Formik } from "formik";
import * as yup from "yup";
import { useNavigate } from "react-router-dom";
import { useDispatch } from "react-redux";
import { setLogin } from "../state";
import axios from "axios";

const initialValues = {
  email: "",
  password: "",
};

//VALIDATION SCHEMA
const loginSchema = yup.object().shape({
  email: yup.string().email().required("Email Required"),
  password: yup.string().min(4).required("Password Required"),
});
function LogIn() {
  const dispatch = useDispatch();
  const navigate = useNavigate();

  const handleFromSubmit = async (values, onSubmitProps) => {
    axios
      .post("http://localhost:3001/auth/login", values)
      .then(
        (res) =>
          dispatch(
            setLogin({
              token: res.data.token,
              user: res.data.user,
            })
          ),
        navigate("/homepage")
      )
      .catch((err) => console.log(err));
  };

  return (
    <>
      <div className="mainbg">
        <div className="left">
          <div className="logo">
            <img src={logo} alt="" />
          </div>
          <div className="form-section">
            <div className="welcometext">WELCOME BACK</div>
            <Formik
              initialValues={initialValues}
              validationSchema={loginSchema}
              onSubmit={handleFromSubmit}
            >
              {({
                values,
                errors,
                touched,
                handleBlur,
                handleSubmit,
                handleChange,
              }) => (
                <form onSubmit={handleSubmit}>
                  <div className="start-align">
                    <input
                      type="text"
                      placeholder="Email"
                      name="email"
                      value={values.email}
                      onChange={handleChange}
                      onBlur={handleBlur}
                    />
                    {errors.email && touched.email ? (
                      <p className="form-error">{errors.email} </p>
                    ) : null}
                    <input
                      type="password"
                      autoComplete="off"
                      placeholder="Password"
                      name="password"
                      value={values.password}
                      onChange={handleChange}
                      onBlur={handleBlur}
                    />
                    <span>
                      {errors.password && touched.password ? (
                        <p className="form-error">{errors.password} </p>
                      ) : (
                        <p></p>
                      )}
                      <p>
                        <a className="forgetPassword" href="">
                          Forget Password
                        </a>{" "}
                      </p>
                    </span>
                  </div>
                  <button type="submit">Submit</button>
                </form>
              )}
            </Formik>

            <p>
              Already have an account? <a href="/signup">SignUp</a>
            </p>
          </div>
        </div>
        <div className="right">
          <p>Where Comfort Meets Convenience:</p>
          <p>Explore Your Dream Residence Today</p>
        </div>
      </div>
    </>
  );
}

export default LogIn;
