import React from "react";
import { Formik } from "formik";
import { useSelector } from "react-redux";

import * as yup from "yup";

const AddPage = () => {
  const userId = useSelector((state) => state.user._id);

  const initialValues = {
    category: "pg",
    no_of_rooms: "1",
    no_of_bathrooms: "1",
    food_categories: "veg",
    image_path: "",
    description: "",
    area: "",
    city: "",
    state: "",
    reviews: "",
  };

  const handleFormSubmit = async (values, onsubmitProps) => {
    // var house = new Object();
    // house.userId = userId;
    var house = `{
      "userId": "64731945130aa77a32a5f652",
      "category": "pg",
      "no_of_rooms": 2,
      "no_of_bathrooms": 1,
      "food_categories": [
          "veg",
          "non veg"
      ],
      "image_path": [
          "https://images.unsplash.com/photo-1586105251261-72a756497a11?ixlib=rb-4.0.3&ixid=M3wxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8fA%3D%3D&auto=format&fit=crop&w=958&q=80"
      ],
      "description": "sabse bada chb 2 bhk",
      "location": {
          "area": "chb",
          "city": "jodhpur",
          "state": "rajasthan"
      },
      "reviews": []
  }`;
  };
  return (
    <>
      <div className="filterhandles">
        <div className="headText">Create Your Own Advertisement</div>
      </div>
      <Formik initialValues={initialValues} onSubmit={handleFormSubmit}>
        {({
          values,
          errors,
          touched,
          handleBlur,
          handleSubmit,
          handleChange,
        }) => (
          <form onSubmit={handleSubmit}>
            <label for="category">Choose a car:</label>
            <select
              name="category"
              value={values.category}
              onChange={handleChange}
            >
              <option>PG</option>
              <option>Hostel</option>
              <option>Flat</option>
              <option>Home</option>
            </select>
            <input
              type="number"
              value={values.no_of_rooms}
              name="no_of_rooms"
              onChange={handleChange}
            />
            <input
              type="number"
              value={values.no_of_bathrooms}
              name="no_of_bathrooms"
              onChange={handleChange}
            />
            <select
              name="food_categories"
              value={values.food_categories}
              onChange={handleChange}
            >
              <option>VEG</option>
              <option>NON VEG</option>
              <option>JAIN FOOD</option>
            </select>
            <input
              type="text"
              value={values.image_path}
              name="image_path"
              onChange={handleChange}
            />
            {/* image
            <input
              type="file"
              value={undefined}
              name="image_path"
              onChange={handleChange}
            /> */}
            desc
            <input
              type="text"
              value={values.description}
              name="description"
              onChange={handleChange}
            />
            area
            <input
              type="area"
              value={values.area}
              name="area"
              onChange={handleChange}
            />
            city
            <input
              type="text"
              value={values.city}
              name="city"
              onChange={handleChange}
            />
            state
            <input
              type="text"
              value={values.state}
              name="state"
              onChange={handleChange}
            />
            <button type="submit">submit</button>
          </form>
        )}
      </Formik>
    </>
  );
};

export default AddPage;
