import React, { useState } from "react";
import { Formik } from "formik";
import { useSelector } from "react-redux";
import { ToastContainer } from "react-toastify";
// import * as yup from "yup";
import Notify from "../../components/Notify";

import firebase from "firebase/compat/app";
import {
  getStorage,
  ref,
  uploadBytesResumable,
  getDownloadURL,
} from "firebase/storage";
import { storage } from "../../firebase";

const AddPage = () => {
  const userId = useSelector((state) => state.user._id);

  const initialValues = {
    category: "pg",
    no_of_rooms: "1",
    no_of_bathrooms: "1",
    food_categories: "veg",
    image_path: "",
    description: "",
    area: "",
    city: "",
    state: "",
    reviews: "",
  };

  const submitData = async (data) => {
    try {
      const res = await fetch("http://localhost:3001/house/addhouse/", {
        method: "POST",
        // headers: {
        //   "Content-Type": "application/json",
        // },
        body: JSON.stringify(data),
      });
      if (res.ok) {
        Notify("Add Created Successfully");
      }
    } catch (e) {
      console.log(e);
    }
  };

  const handleFormSubmit = async (values) => {
    try {
      let file = values.image_path;
      var storagePath = "uploads/" + file.name;

      const storageRef = ref(storage, storagePath);
      const uploadTask = uploadBytesResumable(storageRef, file);
      uploadTask.on(
        "state_changed",
        (snapshot) => {
          // progrss function ....
          const progress =
            (snapshot.bytesTransferred / snapshot.totalBytes) * 100;
          console.log("Upload is " + progress + "% done");
        },
        (error) => {
          // error function ....
          console.log(error);
        },
        () => {
          // complete function ....
          getDownloadURL(uploadTask.snapshot.ref).then((downloadURL) => {
            console.log("File available at", downloadURL);
            console.log(typeof downloadURL);
            var house = `{
              "userId": "${userId}",
              "category": "${values.category}",
              "no_of_rooms": "${values.no_of_rooms}",
              "no_of_bathrooms": "${values.no_of_bathrooms}",
              "food_categories": [
                  "${values.food_categories}"
              ],
              "image_path": [
                  "${downloadURL}"
              ],
              "description": "${values.description}",
              "location": {
                  "area": "${values.area}",
                  "city": "${values.city}",
                  "state": "${values.state}"
              },
              "reviews": []
             }`;

            // submitData(house);
          });
        }
      );
    } catch (e) {
      console.log("poora error", e);
    }
  };

  // const handleFormSubmit = async (values, onsubmitProps) => {
