  //             !(v instanceof Date) &&
  //             !(v instanceof File)
  //           ) {
  //             return f(fd)(k)(v);
  //           } else {
  //             fd.append(k, v);
  //           }
  //         });
  //       }
  //       return fd;
  //     }
  //   )(new FormData())();

  //   try {
  //     var form_data = jsonToFormData (jsonForPost, testJSON);
  //     var house = `{
  //       "userId": "${userId}",
  //       "category": "${values.category}",
  //       "no_of_rooms": "${values.no_of_rooms}",
  //       "no_of_bathrooms": "${values.no_of_bathrooms}",
  //       "food_categories": [
