  //       // },
  //       body: formdata,
  //     });
  //     if (res.ok) {
  //       Notify("Add Created Successfully");
  //     }
  //   } catch (e) {
  //     console.log("error", e);
  //   }
  // };
  return (
    <>
      <ToastContainer />
      <div className="filterhandles">
        <div className="headText">Create Your Own Advertisement</div>
      </div>
      <Formik initialValues={initialValues} onSubmit={handleFormSubmit}>
        {({
          values,
          errors,
          touched,
          handleBlur,
          handleSubmit,
          handleChange,
          setFieldValue,
        }) => (
          <form onSubmit={handleSubmit}>
            <select
              name="category"
              value={values.category}
              onChange={handleChange}
            >
              <option>PG</option>
              <option>Hostel</option>
              <option>Flat</option>
              <option>Home</option>
            </select>
            <input
              type="number"
              value={values.no_of_rooms}
              name="no_of_rooms"
              onChange={handleChange}
            />
            <input
              type="number"
              value={values.no_of_bathrooms}
              name="no_of_bathrooms"
              onChange={handleChange}
            />
            <select
