import React from "react";
import "../styles/login.css";
import "../styles/inputField/inputField1.css";
import logo from "../assets/logo.svg";
import { Formik } from "formik";
import * as yup from "yup";

const initialValues = {
  email: "",
  password: "",
};

//VALIDATION SCHEMA
const loginSchema = yup.object().shape({
  email: yup.string().email().required("Email Required"),
  password: yup.string().min(4).required("Password Required"),
});
function LogIn() {
  //   const { values, errors, touched, handleBlur, handleChange, handleSubmit } =
  //     useFormik({
  //       initialValues: initialValues,
  //       onSubmit: (values) => {
  //         alert(JSON.stringify(values));
  //       },
  //     });

  return (
    <>
      <div className="mainbg">
        <div className="left">
          <div className="logo">
            <img src={logo} alt="" />
          </div>
          <div className="form-section">
            <div className="welcometext">WELCOME BACK</div>
            <Formik
              initialValues={initialValues}
              validationSchema={loginSchema}
              onSubmit={(values) => {
                alert(JSON.stringify(values));
              }}
            >
              {({
                values,
                errors,
                touched,
                handleBlur,
                handleSubmit,
                handleChange,
              }) => (
                <form onSubmit={handleSubmit}>
                  <div className="start-align">
                    <input
                      type="text"
                      placeholder="Email"
                      name="email"
                      value={values.email}
                      onChange={handleChange}
                      onBlur={handleBlur}
                    />
                    {errors.email && touched.email ? (
                      <p className="form-error">{errors.email} </p>
                    ) : null}
                    <input
                      type="text"
                      placeholder="Password"
                      name="password"
                      value={values.password}
                      onChange={handleChange}
                      onBlur={handleBlur}
                    />
                    <span>
                      {errors.email && touched.email ? (
                        <p className="form-error">{errors.password} </p>
                      ) : null}
                      <p>
                        <a className="forgetPassword" href="">
                          Forget Password
                        </a>{" "}
                      </p>
                    </span>
                  </div>
                  <button type="submit">Submit</button>
                </form>
              )}
            </Formik>

            <p>
              Already have an account? <a href="/signup">SignUp</a>
            </p>
          </div>
        </div>
        <div className="right">
          <p>Where Comfort Meets Convenience:</p>
          <p>Explore Your Dream Residence Today</p>
        </div>
      </div>
    </>
  );
}

export default LogIn;
