import "../styles/signup.css";
import "../styles/inputField/inputField1.css";
import logo from "../assets/logo.svg";
import { Formik } from "formik";
import * as yup from "yup";
import { useNavigate } from "react-router-dom";
import { useDispatch } from "react-redux";
import { setLogin } from "../state";
import axios from "axios";
import React from "react";

const initialValues = {
  name: "",
  username: "",
  email: "",
  password: "",
  phone: "",
};

function SignUp() {
  const dispatch = useDispatch();
  const navigate = useNavigate();

  const handleFormSubmit = async (values, onSubmitProps) => {
    axios
      .post("http://localhost:3001/auth/register", values)
      .then((res) => {
        alert(
          "Welcome " +
            res.data.user.name +
            " Your Account is successfully created Please Login."
        );
        navigate("/login");
      })
      .catch((err) => {
        console.log(err);
      });
  };
  return (
    <>
      <div className="mainbg">
        <div className="left">
          <div className="logo">
            <img src={logo} alt="" />
          </div>
          <div className="form-section">
            <div className="welcometext">Create Your Account</div>
            <Formik initialValues={initialValues} onSubmit={handleFormSubmit}>
              {({
                values,
                errors,
                touched,
                handleBlur,
                handleSubmit,
                handleChange,
              }) => (
                <form onSubmit={handleSubmit}>
                  <div className="start-align">
                    <input
                      type="text"
                      placeholder="Name"
                      name="name"
                      value={values.name}
                      onChange={handleChange}
                      onBlur={handleBlur}
                    />
                    <input
                      type="text"
                      placeholder="Username"
                      name="username"
                      value={values.username}
                      onChange={handleChange}
                      onBlur={handleBlur}
                    />
                    <input
                      type="text"
                      placeholder="Email"
                      name="email"
                      value={values.email}
                      onChange={handleChange}
                      onBlur={handleBlur}
                    />
                    {errors.email && touched.email ? (
                      <p className="form-error">{errors.email} </p>
                    ) : null}
                    <input
                      type="password"
                      autoComplete="off"
                      placeholder="Password"
                      name="password"
                      value={values.password}
                      onChange={handleChange}
                      onBlur={handleBlur}
                    />
                    <input
                      type="tel"
                      placeholder="Phone No. (optional)"
                      name="phone"
                      value={values.phone}
                      onChange={handleChange}
                      onBlur={handleBlur}
                    />
                    <span>
                      {errors.password && touched.password ? (
                        <p className="form-error">{errors.password} </p>
                      ) : null}
                    </span>
                  </div>
                  <button type="submit">Submit</button>
                </form>
              )}
            </Formik>

            <p>
              Already have an account? <a href="/login">Login</a>
            </p>
          </div>
        </div>
        <div className="right">
          <p>Where Comfort Meets Convenience:</p>
          <p>Explore Your Dream Residence Today</p>
        </div>
      </div>
    </>
  );
}

export default SignUp;
