import { React, useEffect, useState } from "react";
import { useSelector } from "react-redux";
import "../styles/homepage.css";
import Navbar from "../components/Navbar";
import HpIcons from "../components/HpIcons";
import explore from "../assets/icons/explore.png";
import add from "../assets/icons/add.png";
import setting from "../assets/icons/setting.png";
import logout from "../assets/icons/logout.png";
import bookmark from "../assets/icons/bookmark.png";
import search from "../assets/icons/search.svg";
import ExplorePage from "./nav pages/ExplorePage";
import AddPage from "./nav pages/AddPage";
import BookmarkPage from "./nav pages/BookmarkPage";
import SettingsPage from "./nav pages/SettingsPage";
function HomePage() {
  //USESTATES
  const [houseData, setHouseData] = useState([]);
  const [selectedIndex, setSelectedIndex] = useState(0);

  const changePage = (i) => {
    console.log(selectedIndex);
    setSelectedIndex(i);
  };

  const user = useSelector((state) => state.user);
  const userName = useSelector((state) => state.user.name);

  const icons = [explore, add, bookmark, setting];
  const pages = [
    <ExplorePage />,
    <AddPage />,
    <BookmarkPage />,
    <SettingsPage />,
  ];

  useEffect(() => {
    fetch("http://localhost:3001/house/")
      .then((res) => res.json())
      .then((json) => {
        console.log("json", json);
        setHouseData(json["houses"]);
        console.log("houseVar", houseData);
      })
      .catch((err) => {
        console.log("error", err);
      });
  }, []);
  return (
    <>
      <Navbar />
      <div className="hpmain">
        <div className="hpleft">
          <div className="topIcons">
            {icons.map((icon, index) => (
              <HpIcons
                icon={icon}
                onclick={() => {
                  changePage(index);
                }}
              />
            ))}
            {/* <HpIcons icon={explore} onClick={changePage(0)}></HpIcons> */}
          </div>
          <div className="bottomIcon">
            <HpIcons icon={logout} />
          </div>
        </div>
        <div className="hpright">
          <div className="filterhandles">
            <form action="">
              <div className="search">
                <input
                  type="text"
                  className="hpsearch"
                  placeholder="Enter a location"
                />
                <button className="searchbtn">
                  <img src={search} alt="" />
                </button>
              </div>
            </form>
          </div>
          {pages[selectedIndex]}
        </div>
      </div>
    </>
  );
}

export default HomePage;
